mapper.select:
SELECT UZM.UHEALTH_ZONE_NO , UZM.INSTL_PLACE , UZM.INSTL_ADRES , II.INST_NO FROM UHEALTH_ZONE_MANAGE UZM INNER JOIN INST_INFO II ON UZM.INST_NO = II.INST_NO AND UZM.USE_AT = 'Y' AND UZM.OPEN_AT = 'Y' AND II.USE_AT = 'Y' AND II.SIGUNGU_CD LIKE CONCAT(#{SIDO_CD}, '%')
AND II.SIGUNGU_CD = #{SIGUNGU_CD}
INNER JOIN ( SELECT INST_NO FROM INST_CHARGER_INFO WHERE MBER_NO = #{MBER_NO} AND AUTHOR_CODE = 'CNTC' AND USE_AT = 'Y' ) ICI ON UZM.INST_NO = ICI.INST_NO
UNION ALL SELECT UZM.UHEALTH_ZONE_NO , UZM.INSTL_PLACE , UZM.INSTL_ADRES , II.INST_NO FROM UHEALTH_ZONE_MANAGE UZM INNER JOIN ( SELECT CONCAT(SIDO.IEM_NM, ' ', SIGG.IEM_NM) ADRESS FROM IEM_CODE SIGG INNER JOIN ( SELECT IEM_CODE , IEM_NM FROM IEM_CODE WHERE USE_AT = 'Y' AND GROUP_CODE = 'CM17' ) SIDO ON SIGG.IEM_CODE LIKE CONCAT(SIDO.IEM_CODE, '%') WHERE SIGG.GROUP_CODE = 'CM18' AND SIDO.IEM_CODE = #{SIDO_CD}
AND SIGG.IEM_CODE = #{SIGUNGU_CD}
ORDER BY SIGG.IEM_CODE ) IC ON UZM.INSTL_ADRES LIKE CONCAT(IC.ADRESS, '%') INNER JOIN INST_INFO II ON UZM.INST_NO = II.INST_NO AND UZM.USE_AT = 'Y' AND UZM.OPEN_AT = 'Y' AND II.USE_AT = 'Y' AND II.SIGUNGU_CD IN ('10000', '99999')
INNER JOIN ( SELECT INST_NO FROM INST_CHARGER_INFO WHERE MBER_NO = #{MBER_NO} AND AUTHOR_CODE = 'CNTC' AND USE_AT = 'Y' ) ICI ON UZM.INST_NO = ICI.INST_NO
ORDER BY INSTL_ADRES ASC

