GROUP BY MIC.MBER_NO, MIC.MESURE_CODE, SUBSTRING(MESURE_DT, 1, 8) ) MMIC ON MIC.MBER_NO = MMIC.MBER_NO AND MIC.MESURE_CODE = MMIC.MESURE_CODE AND MIC.MESURE_DT = CONCAT(MMIC.MESURE_DE, MMIC.MESURE_TIME) GROUP BY MIC.MBER_NO, SUBSTRING(MIC.MESURE_DT, 1, 8) ) MICD ON MI.MBER_NO = MICD.MBER_NO

