AND SECSN_AT = 'N' ) MPI ON MIC.MBER_NO = MPI.MBER_NO WHERE MESURE_CODE IN ('6056', '622E', '622F') GROUP BY SUBSTRING(MESURE_DT, 1, 6), MESURE_CODE ORDER BY MESURE_CODE, SUBSTRING(MESURE_DT, 1, 6) ) TBL GROUP BY MESURE_MT

